        static void CreateCountries(List<Account> accounts)
        {
            using (var _proactive = new myproact_testingContext())
            {
                var countries = _proactive.Countries.ToList();
                List<string> countrieslist = new List<string>();
                foreach (var country in countries) { countrieslist.Add(country.CountryName); }
                foreach (var acc in accounts)
                {


                    if (acc.Country != "" && acc.Country != null)
                    {
                        string Countryacc = acc.Country.ToUpper();
                        if (!countrieslist.Contains(Countryacc))
                        {
                            Proactive.Models.Proactive.Country count = new Proactive.Models.Proactive.Country
                            {
                                CountryName = Countryacc,
                                UserId = "349c7e9e-e6fe-4dc2-8f73-61d217b7652c" // Joe
                            };
                            _proactive.Countries.Add(count);
                            countrieslist.Add(Countryacc);

                        }
                    }
                    if (acc.BillingCountry != "" && acc.BillingCountry != null)
                    {
                        string CountryBacc = acc.BillingCountry.ToUpper();
                        if (!countrieslist.Contains(CountryBacc))
                        {
                            Proactive.Models.Proactive.Country countb = new Proactive.Models.Proactive.Country
                            {
                                CountryName = CountryBacc,
                                UserId = "349c7e9e-e6fe-4dc2-8f73-61d217b7652c" // Joe
                            };
                            _proactive.Countries.Add(countb);

                            countrieslist.Add(CountryBacc);
                        }
                    }
                }
                foreach (var con in contacts)
                {
                    if (con.HomeAddressCountry != "" && con.HomeAddressCountry != null)
                    {
                        string CountryHA = con.HomeAddressCountry.ToUpper();
                        if (!countrieslist.Contains(CountryHA))
