        static void CreateContacts(List<AccountsContact> contacts)
        {
            using (var _proactive = new myproact_testingContext())
            {
                foreach (var con in contacts)
                {
                    Proactive.Models.Proactive.Country currentCountry;
                    int? contactCountryID = null;
                    if (con.BusinessAddressCountry != "")
                    {
                        if (con.BusinessAddressCountry == "UK") { currentCountry = _proactive.Countries.Where(c => c.CountryName == "United Kingdom").FirstOrDefault(); }
                        else { currentCountry = _proactive.Countries.Where(c => c.CountryName == con.BusinessAddressCountry).FirstOrDefault(); }
                        if (currentCountry != null) { contactCountryID = currentCountry.CountryId; }
                    }
                    int? uppertitle = null;
                    if (con.Title != "")
                    {
                        var x = _proactive.ContactTitles.Where(m => m.TitleDesc == con.Title.ToUpper().Trim()).FirstOrDefault();
                        uppertitle = x.ContactTitleId;
                    }
                    Org temporg;
                    int? temporgid = null;
                    Account tempac = accounts.Where(m => m.AcId == con.AcId).FirstOrDefault();
                    try
                    {
                        temporg = _proactive.Orgs.Where(m => m.OrgName == tempac.Name && m.TelNo == tempac.Phone1).FirstOrDefault();
                        temporgid = temporg.OrgId;
                    }
                    catch (Exception e) { }
                    Contact tempc = new Contact
                    {
                        FullName = con.FullName,
                        FamilyName = con.LastName,
                        GivenName = con.FirstName,
