        static void CreateTodoes(List<Proactive.Models.Maguire.Activity> activities)
        {
            Dictionary<string, string> TodoTypes = new Dictionary<string, string>();
            TodoTypes.Add("4f675423-aed8-42b3-902e-5d1880d87f52", "Call");
            TodoTypes.Add("0e2a08b9-3c41-4b6f-bb10-b52397a93ece", "General Account Task");
            TodoTypes.Add("25fd11b1-40a5-4458-989c-378d59b961f4", "To-do");
            using (var _proactive = new myproact_testingContext())
            {
                foreach (var act in activities)
                {
                    Proactive.Models.Maguire.AccountsContact MagCont;
                    Proactive.Models.Proactive.Contact ProCont;
                    int? procontid = null;
                    try
                    {
                        MagCont = contacts.Where(m => m.AccId == act.AccId).FirstOrDefault();
                        ProCont = _proactive.Contacts.Where(m => m.FullName == MagCont.Summary && m.Email == MagCont.Email1Address).FirstOrDefault();
                        if (ProCont != null) { procontid = ProCont.ContactId; }
                    }
                    catch (Exception e) { }

                    int? tdcID = null;
                    try
                    {
                        TodoCategory tdc = _proactive.TodoCategories.Where(m => m.TodoTypeName == TodoTypes[act.Type.ToString()]).FirstOrDefault();
                        tdcID = tdc.TodoTypeId;
                    }
                    catch (Exception e) { }


                    Org temporg;
                    int? temporgid = null;
                    try
                    {
                        Account tempac = accounts.Where(m => m.AcId == act.AcId).FirstOrDefault();
                        temporg = _proactive.Orgs.Where(m => m.OrgName == tempac.Name && m.TelNo == tempac.Phone1).FirstOrDefault();
                        temporgid = temporg.OrgId;
                    }
                    catch (Exception e) { }

                    Todo tempto = new Todo
                    {
                        ContactId = procontid,
                        TodoDesc = act.Summary + " " + act.Details,
                        DateAdded = (DateTime)act.CreatedDate,
                        DoBy = (DateTime)act.DueDate,
                        DateDone = act.DateCompleted,
                        MarketingListId = null,
                        TodoType = 0,
                        IsDone = BooltoInt(act.Complete),
                        UserId = ChristineUsID,
                        ExtarnalId = null,
                        OpportunityId = null,
                        ContactToDoFieldId = null,
                        OutlookId = null,
                        ToDoTypeId = tdcID,
                        OrgId = temporgid,
                        AssignedUserId = ChristineUsID,
                        OrgTodoTypeId = null,
                        StatusId = null,
                        Priority = null
                    };
                    _proactive.Todoes.Add(tempto);
                }
                _proactive.SaveChanges();
            }
        }

        static void ConnectAccounts(List<Account> accounts, List<AccountsContact> contacts)
        {
            using (var _proactive = new myproact_testingContext())
            {
                foreach (var acc in accounts)
                {
                    int? MCID = null;
                    List<AccountsContact> mainconts = contacts.Where(m => m.AcId == acc.AcId).ToList();
                    if (mainconts != null)
                    {
                        if (mainconts.Count() == 1)
                        {
                            MCID = _proactive.Contacts.Where(m => m.FullName == mainconts[0].FullName).FirstOrDefault().ContactId;
                            _proactive.Orgs.Where(m => m.OrgName == acc.Summary).FirstOrDefault().MainContactId = MCID;
                        }
                    }

                }
                _proactive.SaveChanges();
            }
        }

